                              <input
                                type="text"
                                className="form-control"
                                name="name"
                                id="name"
                                placeholder="Name"
                              required/>
                            </div>
                          </div>
                          <div className="col-md-6">
                            <div className="form-group">

                              <input
                                type="email"
                                className="form-control"
                                name="email"
                                id="email"
                                placeholder="Email"
                              required/>
                            </div>
                          </div>
                          <div className="col-md-6">
                            <div className="form-group">

                              <input
                                type="text"
                                className="form-control"
                                name="Compname"
                                id="Compname"
                                placeholder="Company name"
                              required/>

                        </div>

                          </div>
                          <div className="col-md-6">
                            <div className="form-group">

                              <input
                                type="number"
                                className="form-control"
                                name="subject"
                                id="subject"
                                placeholder="contact number" country="US"
                              required/>

                        </div>

                          </div>
                          <div className="col-md-12">
                            <div className="form-group">

                              <textarea
                                name="message"
                                className="form-control"
                                id="message"
                                cols="30"
                                rows="4"
                                placeholder="Message"
                              required></textarea>
                            </div>
                          </div>
                          <div className="col-md-12">
                            <div className="form-group">
                              <input
                                type="submit"
                                value="Send Message"
                                className="btn btn-primary"
                              />
                              <div className="submitting"></div>
                            </div>
                          </div>
                        </div>
                      </form>
                    </div>
                  </div>

</div>
</div>

</>
  )
};

export default Introduction;


