/**
 * Used below in {@link createCSSStyleSheet}.
 */
const subdoc = document.implementation.createHTMLDocument();

export function bindElements<
  Binding extends { [key: string | symbol | number]: Element }
>(root: Element, elements: { [key in keyof Binding]: string }): Binding {
  const binding: Binding = {} as Binding;
  for (const [key, selector] of Object.entries(elements)) {
    binding[key as keyof Binding] = root.querySelector(selector);
  }

  return binding;
}

/**
 * Gets all child elements for a given node.
 */
export function getChildElements(node: Node): HTMLElement[] {
  return Array.from(node.childNodes || []).filter(
    (el) => el.nodeType === Node.ELEMENT_NODE
  ) as HTMLElement[];
}

/**
 * Creates an HTML element from a string and returns it.
 */
export function createElementFromString<T extends Element>(
  innerHTML: string
): T {
  const template = document.createElement('template');
  template.innerHTML = innerHTML;
  return template.content.firstElementChild as T;
}

/**
 * Returns a {@link CSSStyleSheet} that can be used to traverse a
 * CSS file.
 *
 * NOTE: We need to use a sub-document to avoid the styles being
 * applied to the current page.
 */
export function createCSSStyleSheet(text: string): CSSStyleSheet {
  const style = subdoc.createElement('style');
  style.textContent = text;
  subdoc.head.appendChild(style);
  return style.sheet;
}

/**
 * Returns if the property has "display: none" set on itself.
 *
 * Reference: https://stackoverflow.com/questions/19669786/check-if-element-is-visible-in-dom
 */
export function isVisible(element: HTMLElement): boolean {
  const style = window.getComputedStyle(element);
  return style.display !== 'none';
}

export type InputFormElement =
  | HTMLInputElement
  | HTMLSelectElement
  | HTMLTextAreaElement;

type ReactInputEvent = Event & {
  simulated: boolean;
};

type ReactInputWrapperState = InputFormElement & {
  _valueTracker?: {
    getValue: () => string;
    setValue: (value: string) => void;
    stopTracking: () => void;
  };
};

/**
 * Sets the value of the given input element and triggers an `input` event.
 *
 * This should also work for React, which overrides the `value` setter with
 * a custom tracker. Due to this, a naive `input.value = value` will not
 * work correctly in React.
 *
 * Reference: https://github.com/facebook/react/blob/c88fb49d37fd01024e0a254a37b7810d107bdd1d/packages/react-dom/src/client/inputValueTracking.js#L53
 * Reference: https://github.com/facebook/react/issues/11488
 */
export function setValue(
  input: InputFormElement | undefined,
  value: string
): void {
  if (!input) {
    return;
  }

  const set = Object.getOwnPropertyDescriptor(
    input.constructor.prototype,
    'value'
  ).set;
  set.call(input, value);

  // Make this work in React 16+.
  const wrapperState = input as ReactInputWrapperState;
  if (wrapperState._valueTracker) {
    wrapperState._valueTracker.setValue('');
  }

  const onInputEvent = new Event('input', {
    bubbles: true
  }) as ReactInputEvent;
  onInputEvent.simulated = true;

  input.dispatchEvent(onInputEvent);

  const onChangeEvent = new Event('change', {
    bubbles: true
  }) as ReactInputEvent;
  onChangeEvent.simulated = true;

  input.dispatchEvent(onChangeEvent);
}

/**
 * Pre-calculate the size of an element, handling cases where the element is not yet rendered
 * @param element - The element to measure
 * @param deep - If true, include node's descendents in calculation. Defaults to false.
 * @returns Object with height and width properties as numbers
 */
export function getElementSize(
  element: HTMLElement,
  deep = false
): {
  height: number;
  width: number;
} {
  let width: number;
  let height: number;
  const elementRect = element.getBoundingClientRect();
  if (
    element.style.display === 'none' ||
    (elementRect.height === 0 && elementRect.width === 0)
  ) {
    const clone = element.cloneNode(deep) as HTMLElement;
    element.parentElement.appendChild(clone);
    clone.style.setProperty('display', 'block', 'important');
    const cloneRect = clone.getBoundingClientRect();
    width = cloneRect.width;
    height = cloneRect.height;
    clone.style.setProperty('display', 'none');
    clone.remove();
  } else {
    width = elementRect.width;
    height = elementRect.height;
  }
  return {
    height: Math.floor(height),
    width: Math.floor(width)
  };
}

/**
 * Adds a style to the current page document for the root CSS from the Mapbox search-js-web package.
 */
export function addDocumentStyle(css: string): void {
  const style = document.createElement('style');
  style.innerHTML = css;
  document.head.appendChild(style);
}
import { createCSSStyleSheet } from './dom';

/**
 * W3C spec:
 *
 * In CSS, identifiers (including element names, classes, and IDs in selectors)
 * can contain only the characters [a-z0-9] and ISO 10646 characters U+00A0 and
 * higher, plus the hyphen (-) and the underscore (_); they cannot start with a
 * digit, or a hyphen followed by a digit. Identifiers can also contain escaped
 * characters and any ISO 10646 character as a numeric code (see next item).
 *
 * For instance, the identifier "B&W?" may be written as "B&W?" or "B\26 W\3F".
 *
 * Reference: https://stackoverflow.com/questions/448981/which-characters-are-valid-in-css-class-names-selectors
 */
const IDENTIFIER_REGEX = new RegExp('[_a-zA-Z]+[_a-zA-Z0-9-]*', 'g');
const CLASS_NAME_REGEX = new RegExp(`\\.${IDENTIFIER_REGEX.source}`, 'g');

/**
 * Of the format @IDENFIFIER (RULE) { }
 *
 * Only catches the media and supports identifiers.
 *
 * Reference: https://developer.mozilla.org/en-US/docs/Web/CSS/At-rule
 */
const CONDITION_RULE_REGEX = new RegExp(
  `^\\s*(@(?:media|supports)[^{]*){(.*)}\\s*$`
);

export function transformClassSelectors(
  css: string,
  transform: (className: string) => string
): string {
  return css.replace(CLASS_NAME_REGEX, (className: string) => {
    return '.' + transform(className.slice(1));
  });
}

/**
 * Returns a version of the CSS string with each class name transformed
 * by {@link transform}.
 *
 * The main reason why this function exists is to allow for CSS class
 * encapsulation without Shadow DOM.
 */
export function transformCSSClassRules(
  text: string,
  transform: (className: string) => string
): string {
  // Create a CSSStyleSheet so we can traverse the CSS rules,
  // without having to parse ourselves.
  const sheet = createCSSStyleSheet(text);
  const rules = sheet.cssRules;

  /**
   * No guarantees of in-place mutation.
   */
  function transformCSSRule(rule: CSSRule): string {
    /**
     * [CSSStyleRule](https://developer.mozilla.org/en-US/docs/Web/API/CSSStyleRule)
     * represents a single CSS declaration block, like `h1 { color: red; }`.
     *
     * Make sure to transform the "selector", which in the example above is "h1".
     */
    if (rule instanceof CSSStyleRule) {
      const selector = transformClassSelectors(rule.selectorText, transform);
      return `${selector} { ${rule.style.cssText} }`;
    }

    /**
     * Condition-rules are like `@media print { ... }`, or `@supports (display: grid) { ... }`.
     *
     * This should be covered by [CSSConditionRule](https://developer.mozilla.org/en-US/docs/Web/API/CSSConditionRule)
     * however browser support for CSSConditionRule is not great: namely, Safari
     * versions earlier than 14.1 (early 2021) do not support it.
     *
     * Because of this, we need to traverse the text of the at-rule with
     * a regular expression.
     *
     * Useful helper: https://regexr.com/
     */
    const atRule = CONDITION_RULE_REGEX.exec(rule.cssText.split('\n').join(''));
    if (atRule && atRule.length > 2) {
      const rule = atRule[1];
      const contents = atRule[2];

      // Recursively transform the contents of the at-rule.
      return `${rule} { ${transformCSSClassRules(contents, transform)} }`;
    }

    /**
     * Otherwise, just pass through fonts, animations, etc...
     */
    return rule.cssText;
  }

  // Construct a new CSS string by traversing the rules and serializing
  // them to a string.
  let style = '';
  for (const rule of Array.from(rules)) {
    style += transformCSSRule(rule) + '\n\n';
  }

  return style.trim();
}

/**
 * Manipulates {@param content} in place, with each class attribute transformed
 * by {@link transform}.
 *
 * The main reason why this function exists is to allow for CSS class
 * encapsulation without Shadow DOM.
 */
export function transformDOMClassAttributes(
  content: HTMLElement,
  transform: (className: string) => string
): HTMLElement {
  // Perform the transformation on each class attribute in-place,
  // on the clone.
  const elements = Array.from(content.querySelectorAll('[class]'));
  // Add the current node!
  elements.push(content);

  for (const element of elements) {
    const { classList } = element;
    for (const className of Array.from(classList)) {
      classList.remove(className);
      classList.add(transform(className));
    }
  }

  // Return the element.
  return content;
}
/* eslint-disable @typescript-eslint/no-explicit-any */

import { SessionToken } from '@mapbox/search-js-core';
import { MAPBOX_DOMAINS } from '../constants';

/**
 * Returns a random ID that is valid as a CSS identifier.
 *
 * CSS identifiers cannot start with a number, so we prefix the ID with `mbx`.
 */
export function randomValidID(): string {
  return `mbx` + new SessionToken().id.slice(0, 8);
}

/**
 * Try to parse as a JSON object, returning `null` if it fails.
 */
export function tryParseJSON<T>(json: string): T | null {
  try {
    return JSON.parse(json);
  } catch (e) {
    return null;
  }
}

export function deepEquals<T>(a: T, b: T): boolean {
  if (a == null || b == null) {
    return a === b;
  }

  if (typeof a !== 'object' || typeof b !== 'object') {
    return a === b;
  }

  const aKeys = Object.keys(a);
  const bKeys = Object.keys(b);

  if (aKeys.length !== bKeys.length) {
    return false;
  }

  for (const key of aKeys) {
    if (!deepEquals(a[key], b[key])) {
      return false;
    }
  }

  return true;
}

/**
 * Round a number to a given number of digits after the decimal
 * @param num - Number to round
 * @param decimalPlaces - Number of places after the decimal to round to
 * @returns Rounded decimal number
 */
export function round(num: number, decimalPlaces: number): number {
  const factorOfTen = Math.pow(10, decimalPlaces);
  return Math.round(num * factorOfTen) / factorOfTen;
}

/**
 * Returns true if a hostname matches a local server protocol
 * @param hostname - A string representing window.location.hostname
 * @returns A boolean value
 */
export function isLocalServer(hostname: string): boolean {
  return Boolean(
    hostname.match(
      /localhost|[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}|::1|\.local|^$/gi
    )
  );
}

/**
 * Returns true if a hostname matches a Mapbox domain
 * @param hostname - A string representing window.location.hostname
 * @returns A boolean value
 */
export function isMapboxDomain(hostname: string): boolean {
  return Boolean(MAPBOX_DOMAINS.some((domain) => hostname.includes(domain)));
}
import mapboxgl from 'mapbox-gl';

/**
 * This is slightly higher than the default value of 1.2.
 * https://docs.mapbox.com/mapbox-gl-js/api/map/#map#flyto
 * We use a high speed value for cypress tests to increase test speed and improve test stability.
 */
export const FLY_TO_SPEED = 1.4;

/**
 * Gets the center/zoom for a given bounding box.
 *
 * @param delta - Subtracted from the zoom level so users can fully see
 * the bounding box. This is a hack to add padding.
 */
export function bboxViewport(
  map: mapboxgl.Map,
  bounds: mapboxgl.LngLatBoundsLike,
  delta = 0.5
): mapboxgl.FlyToOptions {
  // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
  const { center, zoom } = map.cameraForBounds(bounds)!;
  const transformedZoom = Math.max(zoom - delta, 0);

  return {
    center,
    zoom: transformedZoom,
    speed: FLY_TO_SPEED
  };
}

/**
 * Gets the maximum zoom level for a [Mapbox Search API](https://docs.mapbox.com/api/search/search/)
 * or [Mapbox Geocoding API](https://docs.mapbox.com/api/search/geocoding/) place type.
 *
 * @returns The maximum zoom level for the given place type, `16` if the type is not supported.
 */
export function getMaxZoom(placeType: string): number {
  switch (placeType) {
    case 'street':
      return 15;
    case 'neighborhood':
    case 'postcode':
    case 'locality':
    case 'oaza':
      return 14;
    case 'place':
    case 'city':
      return 13;
    case 'district':
      return 9;
    case 'region':
    case 'prefecture':
      return 6;
    case 'country':
      return 4;
    default:
      return 16;
  }
}

/**
 * Gets the base URL for the Static Images API for a given user style
 * {@link https://docs.mapbox.com/api/maps/static-images/}
 * @param username The username of the account to which the style belongs
 * @param styleId The ID of the style from which to create a static map
 * @returns
 */
export function getStaticBaseUrl(username: string, styleId: string): string {
  return `https://api.mapbox.com/styles/v1/${username}/${styleId}/static/`;
}
import { createElementFromString } from './utils/dom';
import { getStaticBaseUrl } from './utils/map';

export const STATIC_BASE_URL_SATELLITE = getStaticBaseUrl(
  'mapbox',
  'satellite-streets-v11'
);

export const AUTOFILL_SKU_TOKEN_PREFIX = '20d01';

export const MAPBOX_DOMAINS = ['mapbox.com', 'mapbox.cn', 'tilestream.net'];

export const LISTBOX_TEMPLATE =
  createElementFromString<HTMLTemplateElement>(/* html */ `
<template>
  <div class="MapboxSearch">
    <div class="Label" role="label" aria-live="polite" aria-atomic="true">
    </div>
    <div class="Results" aria-hidden="true">
      <div class="ResultsList" role="listbox">
      </div>
      <div class="ResultsAttribution" aria-hidden="true">
        <a href="https://www.mapbox.com/search-service" target="_blank" tabindex="-1">
          Powered by Mapbox
        </a>
      </div>
    </div>
  </div>
</template>
`);

export const LISTBOX_SUGGESTION_TEMPLATE =
  createElementFromString<HTMLTemplateElement>(/* html */ `
<template>
  <div class="Suggestion" role="option" tabindex="-1">
    <div class="SuggestionIcon" aria-hidden="true"></div>
    <div class="SuggestionText">
      <div class="SuggestionName"></div>
      <div class="SuggestionDesc"></div>
    </div>
  </div>
</template>
`);
/* eslint-disable custom-elements/expose-class-on-global */
/* eslint-disable custom-elements/define-tag-after-class-definition */
import {
  transformClassSelectors,
  transformCSSClassRules,
  transformDOMClassAttributes
} from '../utils/class_name_transformers';

import { randomValidID } from '../utils';

/**
 * Custom HTML element that uses the "Scoped DOM" approach,
 * which is not really Shadow DOM, but a way to encapsulate CSS classes
 * without Shadow DOM.
 *
 * This is a workaround for the fact that ARIA attributes are identified by
 * IDs, and cannot be shared between Shadow DOM and non-Shadow DOM (Light DOM)
 * elements due to encapsulation.
 *
 * Examples are an <input> element and its corresponding popover.
 *
 * The "Scoped DOM" approach is to encapsulate CSS classes by transforming
 * the class attribute of each element, prefixing each class with a
 * random UUID.
 *
 * Goals of the "Scoped DOM":
 *
 * - Once the [Accessibility Object Model (AOM)](https://wicg.github.io/aom/explainer.html) is finalized,
 *   replace all uses of {@link MapboxHTMLScopedElement} with {@link MapboxHTMLShadowElement}, and use
 *   AOM instead of IDREFs for ARIA attributes.
 *
 * - The Scoped DOM should be self-contained and work well enough that we don't
 *   need to constantly work around it.
 *
 * - The Scoped DOM should have extensive testing.
 */
export class HTMLScopedElement<
  Events extends { [key: number | string | symbol]: Event } = Record<
    string,
    Event
  >
> extends HTMLElement {
  // Seed to use for class name prefixing.
  #seed = randomValidID();

  protected get template(): HTMLTemplateElement {
    return null;
  }

  protected get templateStyle(): string {
    return null;
  }

  protected get templateUserStyle(): string {
    return null;
  }

  #templateUserStyleElement: HTMLStyleElement;

  /**
   * `clonedCallback` should be a part of the Web Components spec, but sadly
   * it is not.
   *
   * This is detected in {@link connectedCallback} below if the seed has changed.
   */
  clonedCallback(oldSeed: string, newSeed: string): void {
    // New transform function that replaces any instances of the old
    // seed with the new one.
    const seedTransform = (className: string) =>
      className.replace(oldSeed, newSeed);

    transformDOMClassAttributes(this, seedTransform);

    const styles = Array.from(this.querySelectorAll<HTMLStyleElement>('style'));
    for (const style of styles) {
      style.textContent = transformClassSelectors(
        style.textContent,
        seedTransform
      );
    }

    // Find and repopulate #templateUserStyleElement.
    if (styles.length) {
      this.#templateUserStyleElement = styles[styles.length - 1];
    }

    // Replace id prefix for all sub-nodes
    const nodesWithId = Array.from(this.querySelectorAll(`[id^="${oldSeed}"]`));
    for (const node of nodesWithId) {
      node.id = node.id.replace(oldSeed, newSeed);
    }
  }

  connectedCallback(): void {
    // Return if we already have children.
    if (this.childElementCount > 0) {
      const oldSeed = this.dataset.seed;
      const newSeed = this.#seed;

      // If we have a different seed than before, say from a clone,
      // then we need to update any templates and styles.
      if (oldSeed && oldSeed !== newSeed) {
        this.clonedCallback(oldSeed, newSeed);

        // Finally, make sure the dataset has the new seed.
        this.dataset.seed = newSeed;
      }

      return;
    }

    this.dataset.seed = this.#seed;

    // Use the Light DOM to contain our inner elements,
    // and do CSS class obfuscation in the "Scoped DOM."
    const template = this.template;
    if (template) {
      const element = this.prepareTemplate(template);
      this.appendChild(element);
    }

    const templateStyle = this.templateStyle;
    if (templateStyle) {
      const style = document.createElement('style');
      style.textContent = this.prepareCSS(templateStyle);
      this.appendChild(style);
    }

    const userStyle = document.createElement('style');
    if (this.templateUserStyle) {
      userStyle.textContent = this.prepareCSS(this.templateUserStyle);
    }
    this.appendChild(userStyle);
    this.#templateUserStyleElement = userStyle;
  }

  /**
   * Transform function for {@link transformCSSClassRules} and
   * {@link transformDOMClassAttributes}.
   */
  #transform = (className: string): string => {
    return `${this.#seed}--${className}`;
  };

  prepareTemplate(template: HTMLTemplateElement): HTMLElement {
    const element = template.content.firstElementChild;
    return transformDOMClassAttributes(
      element.cloneNode(true) as HTMLElement,
      this.#transform
    );
  }

  prepareCSS(css: string): string {
    return transformCSSClassRules(css, this.#transform);
  }

  updateTemplateUserStyle(style: string): void {
    if (!this.#templateUserStyleElement) {
      return;
    }
    this.#templateUserStyleElement.textContent = this.prepareCSS(style);
  }

  querySelector<E extends Element = Element>(selectors: string): E {
    return super.querySelector(
      transformClassSelectors(selectors, this.#transform)
    );
  }

  querySelectorAll<E extends Element = Element>(
    selectors: string
  ): NodeListOf<E> {
    return super.querySelectorAll(
      transformClassSelectors(selectors, this.#transform)
    );
  }

  addEventListener<K extends keyof Events>(
    type: K,
    listener: (this: HTMLFormElement, ev: Events[K]) => unknown,
    options?: boolean | AddEventListenerOptions
  ): void;
  addEventListener(
    type: string,
    listener: EventListenerOrEventListenerObject,
    options?: boolean | AddEventListenerOptions
  ): void {
    super.addEventListener(type, listener, options);
  }

  removeEventListener<K extends keyof Events>(
    type: K,
    listener: (this: HTMLFormElement, ev: Events[K]) => unknown,
    options?: boolean | EventListenerOptions
  ): void;
  removeEventListener(
    type: string,
    listener: EventListenerOrEventListenerObject,
    options?: boolean | EventListenerOptions
  ): void {
    super.removeEventListener(type, listener, options);
  }

  dispatchEvent<K extends keyof Events>(event: Events[K]): boolean {
    return super.dispatchEvent(event);
  }
}
