export interface Theme {
  /**
   * CSS variables to be applied to the control.
   */
  variables?: ThemeVariables;
  /**
   * SVG icon overrides to be applied to the control.
   */
  icons?: ThemeIcons;
  /**
   * Raster image URLs to be applied to the control.
   */
  images?: ThemeImages;
  /**
