type CSSText = string;

/**
 * Applies {@link Theme} to the given HTML element as CSS variables
 * in the style attribute.
 */
export function getThemeCSS(rootSelector: string, theme: Theme = {}): CSSText {
  const variables = {
    // Make sure to shallow apply default variables.
    ...DEFAULT_THEME.variables,
    ...(theme.variables || {})
  };

  let cssText = theme.cssText || '';
  let rootVariables = '';

  for (const [key, value] of Object.entries(variables)) {
    // Is a literal, not an expression.
    if (!Array.isArray(value)) {
      rootVariables += `--${key}: ${value};`;
      continue;
    }

    // Is an expression; only support "mobile" for now.
    if (value[0] !== 'mobile') {
      const valueStr = JSON.stringify(value);
      throw new Error(
        `Unsupported expression in theme variables: ${key} ${valueStr}`
      );
    }

    const [, mobileValue, desktopValue] = value;

    // On mobile, force the mobile value.
    cssText += `${MOBILE_MEDIA_QUERY} { ${rootSelector} { --${key}: ${mobileValue} !important; } }`;
    // Our default is still the desktop value.
    rootVariables += `--${key}: ${desktopValue};`;
  }

  return cssText + `${rootSelector} { ${rootVariables} }`;
}

export function getIcon<I extends keyof Theme['icons']>(
  iconName: I,
  theme: Theme = {}
): string | undefined {
  const icons = {
    ...DEFAULT_THEME.icons,
    ...(theme.icons || {})
  };

  const svgString = icons[iconName];
  return svgString;
}

export function getImage<I extends keyof Theme['images']>(
  imageName: I,
  theme: Theme = {}
): string | undefined {
  const images = {
    ...DEFAULT_THEME.images,
    ...(theme.images || {})
  };

  const imgString = images[imageName];
  return imgString;
}
import {
  computePosition,
  autoUpdate,
  ComputePositionConfig,
  flip,
  offset
} from '@floating-ui/dom';

/**
 * @typedef PopoverOptions
 */
export interface PopoverOptions {
  /**
   * Positions the popover above or below the reference element. Defaults to 'bottom-start'.
   */
  placement: 'top-start' | 'bottom-start';
  /**
   * If true, the popover will flip to the opposite side of the reference element to try to keep it in view when scrolling out of frame. Defaults to false.
   */
  flip: boolean;
  /**
   * The distance gap between the popover and the reference element. Defaults to 5px.
   */
  offset: number;
}

/**
 * Wrapper around floating-ui controls to manage popover positioning and lifecycle.
 */
export class Popover {
  /**
   * The element around which the popover is positioned, e.g. the `<input>` search box
   */
  referenceEl: HTMLElement;
  /**
   * The floating popover element, e.g. the results box
   */
  floatingEl: HTMLElement;

  #options: PopoverOptions;

  /**
   * Cleans up the popover instance and any side effects
   */
  destroy: () => void;

  #defaultOptions: PopoverOptions = {
    placement: 'bottom-start',
    flip: false,
    offset: 10
  };

  constructor(
    referenceEl: HTMLElement,
    floatingEl: HTMLElement,
    options?: Partial<PopoverOptions>
  ) {
    this.referenceEl = referenceEl;
    this.floatingEl = floatingEl;
    this.#options = { ...this.#defaultOptions, ...options };
    this.destroy = autoUpdate(this.referenceEl, this.floatingEl, this.update);
  }

  /**
   * Positioning options for the popover element.
   */
  get options(): PopoverOptions {
    return this.#options;
  }
  set options(newOptions: Partial<PopoverOptions>) {
    this.#options = { ...this.#options, ...newOptions };
  }

  /**
   * Recomputes the popover position one time
   */
  update = async (): Promise<void> => {
    const config: Partial<ComputePositionConfig> = {
      placement: this.options.placement,
      middleware: [
        offset(this.options.offset),
        this.options.flip && flip()
      ].filter(Boolean)
    };
    const { x, y } = await computePosition(
      this.referenceEl,
      this.floatingEl,
      config
    );
    Object.assign(this.floatingEl.style, {
      left: `${x}px`,
      top: `${y}px`
    });
  };
}
/**
 * `MapboxHTMLEvent` is an event dispatched by the Search Box and Autofill
 * components.
 *
 * @example
 * ```
 * const search = new MapboxSearchBox();
 * search.addEventListener('retrieve', (event: MapboxHTMLEvent<Suggestion>) => {
 *  console.log(event.detail);
 * });
 */
export class MapboxHTMLEvent<T> extends CustomEvent<T> {
  constructor(type: string, detail?: T) {
    super(type, {
      composed: true,
      detail
    });
  }

  /**
   * Returns a shallow copy of the event. This method is useful for
   * manually bubbling events from a connected component.
   *
   * @param newTarget - Optional event target to assign to the clone
   *
   * @example
   * `MapboxSearchBox` will call `event.clone()` on the `retrieve`
   * event from `MapboxSearchListbox`, and then dispatch the cloned event.
   */
  clone(newTarget?: EventTarget): MapboxHTMLEvent<T> {
    const eventClone = new MapboxHTMLEvent(this.type, this.detail);
    if (newTarget) {
      Object.defineProperty(eventClone, 'target', { value: newTarget });
    }
    return eventClone;
  }
}
export const LENGTH_MESSAGE = 'Type in 2 or more characters for results.';

export const KEYBOARD_NAVIGATION_GUIDE_MESSAGE =
  'When autocomplete results are available use up and down arrows to review and enter to select. Touch device users, explore by touch or with swipe gestures.';

export const NO_SEARCH_RESULTS_MESSAGE = 'No search results.';

export const getSuggestionSelectedMessage = (
  address: string,
  numberOfResults: number,
  currentIndex: number
): string =>
  `${numberOfResults} ${
    numberOfResults === 1 ? 'result is' : 'results are'
  } available. ${address}. ${currentIndex} of ${numberOfResults} is selected.`;

export const getSuggestionsReadyMessage = (numberOfResults: number): string =>
  `${numberOfResults} ${
    numberOfResults === 1 ? 'result is' : 'results are'
  } available. Use up and down arrows to review and enter to select. Touch device users, explore by touch or with swipe gestures.`;
import {
  AutofillSuggestion,
  GeocodeFeature,
  Suggestion as SearchSuggestion
} from '@mapbox/search-js-core';
import {
  getSuggestionSelectedMessage,
  getSuggestionsReadyMessage,
  KEYBOARD_NAVIGATION_GUIDE_MESSAGE,
  LENGTH_MESSAGE,
  NO_SEARCH_RESULTS_MESSAGE
} from './messages';
/**
 * This is to make sure our custom "buttons" have appropriate
 * keyboard controls for WAI-ARIA.
 *
 * Reference: https://www.w3.org/TR/wai-aria-practices-1.1/#keyboard-interaction-3
 */
export function ariaButtonKeyDown(e: KeyboardEvent): void {
  const el = e.currentTarget as HTMLElement;

  if (e.key === ' ' || e.key === 'Enter') {
    e.preventDefault();
    e.stopPropagation();

    // Simulate a click event.
    el.dispatchEvent(
      new MouseEvent('click', {
        bubbles: true,
        composed: true
      })
    );
  }
}

const ARIA_DESCRIPTION_ID = 'address-autofill__description';

/**
 * Builds element that may contain different messages for VoiceOver utilities.
 * The element has necessary aria-* specific attributes and visually hidden.
 */
export const createAriaLiveElement = (): HTMLDivElement => {
  const container = document.createElement('div');
  container.setAttribute('aria-live', 'polite');
  container.setAttribute('aria-atomic', 'true');
  container.setAttribute('role', 'status');
  container.setAttribute(
    'style',
    'border: 0px;clip: rect(0px, 0px, 0px, 0px);height: 1px;margin-bottom: -1px;margin-right: -1px;overflow: hidden;padding: 0px;position: absolute;white-space: nowrap;width: 1px;'
  );

  const description = document.createElement('div');
  description.setAttribute('id', ARIA_DESCRIPTION_ID);

  container.appendChild(description);

  return container;
};

export const setLiveRegionMessage = (message: string): void => {
  const description: HTMLInputElement =
    document.body.querySelector(`[id="${ARIA_DESCRIPTION_ID}"]`) ?? null;
  if (description) {
    description.textContent = message;
  }
};

/**
 * Calculates the message for autofill component to be announced by VoiceOver utilities depending on its state.
 * @example
 * "Type in 2 or more characters for results." (When input text length is less than 2 characters)
 */
export const getAriaMessage = (
  searchValue: string,
  suggestions: (SearchSuggestion | AutofillSuggestion | GeocodeFeature)[],
  selectedIndex: number | undefined
): string => {
  let ariaMessage = null;

  const noResults = !suggestions || suggestions.length === 0;

  if (searchValue?.length < 2) {
    ariaMessage = LENGTH_MESSAGE + ' ' + KEYBOARD_NAVIGATION_GUIDE_MESSAGE;
  } else if (noResults) {
    ariaMessage = NO_SEARCH_RESULTS_MESSAGE;
  } else if (selectedIndex !== undefined) {
    const suggestion = suggestions[selectedIndex];
    const placeName =
      suggestion.address ||
      (suggestion as any).full_address ||
      (suggestion as any).feature_name;

    ariaMessage = getSuggestionSelectedMessage(
      placeName,
      suggestions.length,
      selectedIndex + 1
    );
  } else {
    ariaMessage = getSuggestionsReadyMessage(suggestions.length);
  }

  return ariaMessage;
};
import {
  AutofillFeatureSuggestion,
  AutofillSuggestion,
  GeocodeFeature,
  Suggestion as SearchSuggestion,
  featureToSuggestion
} from '@mapbox/search-js-core';
import { InputFormElement, isVisible, setValue } from './dom';

/**
 * A list of WHATWG autofill tokens relevant to addresses. These will be used to
 * set the `autocomplete` attribute on the input element.
 *
 * Reference: https://html.spec.whatwg.org/multipage/form-control-infrastructure.html#autofilling-form-controls:-the-autocomplete-attribute
 */
type AutofillTokens =
  | 'street-address'
  | 'address-line1'
  | 'address-line2'
  | 'address-line3'
  | 'address-level4'
  | 'address-level3'
  | 'address-level2'
  | 'address-level1'
  | 'country'
  | 'country-name'
  | 'postal-code';

export const AUTOFILL_TOKENS = new Set<AutofillTokens>([
  'street-address',
  'address-line1',
  'address-line2',
  'address-line3',
  'address-level4',
  'address-level3',
  'address-level2',
  'address-level1',
  'country',
  'country-name',
  'postal-code'
]);

/**
 * A list of WHATWG autofill tokens we can safely ignore when traversing
 * `autocomplete` attributes.
 */
const AUTOFILL_SKIP_TOKENS = new Set(['off', 'on', 'true', 'false']);

/**
 * Finds the parent form element of the given element, if any.
 *
 * @returns The parent form element, or `null` if none was found.
 */
export function findParentForm(el: Element): HTMLFormElement | null {
  let node = el.parentNode;
  while (node) {
    if (node instanceof HTMLFormElement) {
      return node;
    }
    node = node.parentNode;
  }

  return null;
}

/**
 * Finds input elements that are eligible to be used for Address Autofill.
 * Eligible inputs include those with standard WHATWG autocomplete attribute values, either 'address-line1' or 'street-address'
 * @param form - If specified, searches within the given form instead of the whole document
 * @returns Array of input elements
 */
export function findAddressInputs(form?: HTMLFormElement): HTMLInputElement[] {
  const parent = form || document;
  return Array.from(
    parent.querySelectorAll<HTMLInputElement>(
      'input[autocomplete~="address-line1"], input[autocomplete~="street-address"]'
    )
  );
}

const SECTION = 'section-';

const SECTION_DEFAULT = 'section-default';
const SECTION_SHIPPING = 'section-shipping';
const SECTION_BILLING = 'section-billing';

type FormStructure = {
  input: InputFormElement;
  section: string;
  field: AutofillTokens;
}[];

/**
 * Implementation of "autocomplete" {@link FormStructure} that matches the
 * WHATWG spec and somewhat Chromium.
 *
 * `billing` and `shipping` are treated as special sections. The {@link SECTION_SHIPPING}
 * and {@link SECTION_BILLING} symbols are used to identify them.
 *
 * Any inputs without an `autocomplete` attribute are skipped.
 *
 * Reference: https://html.spec.whatwg.org/multipage/form-control-infrastructure.html#autofill
 * Reference: https://github.com/chromium/chromium/blob/18a1302acbf920bc692ec6bd986cacd41ae9c25c/components/autofill/core/browser/form_structure.cc#L1503
 */
export function parseFormStructure(form: HTMLFormElement): FormStructure {
  const inputs = Array.from(
    form.querySelectorAll<InputFormElement>('[autocomplete]')
  ).filter((el) => {
    const tagName = el.tagName.toLowerCase();
    return (
      tagName === 'input' || tagName === 'select' || tagName === 'textarea'
    );
  });

  const res: FormStructure = [];

  for (const input of inputs) {
    if (!isVisible(input)) {
      continue;
    }

    // Note: HTML <input> element has an HTMLInputElement.autocomplete reflection
    // property, but <select> elements do not.
    //
    // This does **not** mean that autocomplete properties are not valid on
    // selects. I verified this on a popular e-commerce checkout page.
    const autocomplete = input.getAttribute('autocomplete') || '';
    if (!autocomplete || AUTOFILL_SKIP_TOKENS.has(autocomplete)) {
      continue;
    }

    const tokens = autocomplete.toLowerCase().split(' ');

    // The tokens are parsed in reverse order. The expected pattern is:
    // [section-*] [shipping|billing] [type_hint] field_type

    // Address tokens can be a maximum length of 3. We don't need to check [type_hint].
    if (tokens.length > 3) {
      continue;
    }

    // The last token should be a part of our scheme.
    const field = tokens[tokens.length - 1] as AutofillTokens;
    if (!AUTOFILL_TOKENS.has(field)) {
      continue;
    }
    tokens.pop();

    let section: symbol | string = SECTION_DEFAULT;

    // The second to last token /could/ be 'shipping' or 'billing'.
    if (tokens.length) {
      const sectionToken = tokens[tokens.length - 1];

      if (sectionToken === 'shipping') {
        section = SECTION_SHIPPING;
        tokens.pop();
      }

      if (sectionToken === 'billing') {
        section = SECTION_BILLING;
        tokens.pop();
      }
    }

    // The remaining tokens could be a section.
    if (tokens.length) {
      const sectionToken = tokens[tokens.length - 1];
      if (sectionToken.startsWith(SECTION)) {
        section = sectionToken;
      }
    }

    res.push({
      input,
      section,
      field
    });
  }

  return res;
}

export type AutofillInputMap = { [key in AutofillTokens]?: InputFormElement };
/**
 * Object mapping WHATWG autocomplete attribute values to corresponding address component strings.
 *
 * @typedef AutofillValueMap
 * @example
 * ```typescript
 * {
 *   "street-address"?: string;
 *   "address-line1"?: string;
 *   "address-line2"?: string;
 *   "address-line3"?: string;
 *   "address-level4"?: string;
 *   "address-level3"?: string;
 *   "address-level2"?: string;
 *   "address-level1"?: string;
 *   country?: string;
 *   "country-name"?: string;
 *   "postal-code"?: string;
 * }
 * ```
 */
export type AutofillValueMap = { [key in AutofillTokens]?: string };

/**
 * Reference: [chromium.md](../../../../docs/search_js_web/chromium.md)
 *
 * Implementation of autofill that closely matches Chromium, with a few noted differences:
 *
 * 1. There is no hueristic for inferring a match, so the
 *    WHATWG "autocomplete" attribute is required.
 * 2. This parser, unlike Chromium, doesn't allow more than one token type per
 *    section. Chromium **only** allows this if they are in the same order.
 */
export function findAddressAutofillInputs(
  form: HTMLFormElement,
  ref: HTMLInputElement
): AutofillInputMap {
  // Use an array-of-structs to improve performance.
  // Reference: https://en.wikipedia.org/wiki/AoS_and_SoA#:~:text=Structure%20of%20arrays%20(SoA)%20is,one%20parallel%20array%20per%20field.
  const logicalSections: AutofillInputMap[] = [];
  const logicalSectionSections: (string | symbol)[] = [];

  // Parse the form structure, then go through each node to create
  // logical sections.
  const formStructure = parseFormStructure(form);

  // When we traverse through the form structure, we can check the input against
  // ref to find the logical section we actually want.
  let foundSection: AutofillInputMap = null;

  for (const { input, section, field } of formStructure) {
    let lastIndex = logicalSections.length - 1;

    // 1. If we have no logical sections, create a new section.
    // 2. If the current section is different from the last section, create a new section.
    // 3. If the current field already exists in the last section, create a new section.
    let createNewSection = false;
    if (!logicalSections.length) {
      createNewSection = true;
    } else if (logicalSectionSections[lastIndex] !== section) {
      createNewSection = true;
    } else if (logicalSections[lastIndex][field]) {
      createNewSection = true;
    }

    if (createNewSection) {
      // If we have already found a logical section, creating new sections
      // just wastes time.
      if (foundSection) {
        break;
      }

      logicalSections.push({
        [field]: input
      });

      logicalSectionSections.push(section);
      // Make sure to update lastIndex.
      lastIndex++;
    } else {
      logicalSections[lastIndex][field] = input;
    }

    if (input === ref) {
      foundSection = logicalSections[lastIndex];
    }
  }

  return foundSection ?? {};
}

/**
 * Fills in the address form using HTML autocomplete attributes.
 *
 * This does a mapping from {@link AdministrativeUnitTypes} to corresponding WHATWG autocomplete types.
 *
 * Reference:
 * https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/autocomplete#values
 * https://docs.mapbox.com/api/search/search/#administrative-unit-types
 */
export function setFormAutofillValues(
  form: HTMLFormElement,
  ref: HTMLInputElement,
  suggestion: AutofillSuggestion
): void {
  // Find the inputs that we should fill in.
  const map = findAddressAutofillInputs(form, ref);

  const streetAddress = [
    suggestion.address_line1,
    suggestion.address_line2,
    suggestion.address_line3
  ]
    .filter((part) => Boolean(part))
    .join(', ');

  setValue(map['street-address'], streetAddress);
  setValue(map['address-line1'], suggestion.address_line1 || '');

  setValue(map['address-level1'], suggestion.address_level1 || '');
  setValue(map['address-level2'], suggestion.address_level2 || '');
  setValue(map['address-level3'], suggestion.address_level3 || '');

  // Country in WHATWG refers to the country code, not the name.
  // Example: 'jp' instead of 'Japan'.

  // Note: Make sure to match case here. The country code is lower-case.
  const countryCode =
    suggestion.country_code || suggestion.metadata?.iso_3166_1 || '';
  if (map.country && map.country instanceof HTMLSelectElement) {
    const firstOption = map.country.querySelector(`option`).value;
    const isUpperCase = firstOption === firstOption.toUpperCase();

    setValue(
      map['country'],
      isUpperCase ? countryCode.toUpperCase() : countryCode
    );
  } else {
    setValue(map['country'], countryCode);
  }

  setValue(map['country-name'], suggestion.country || '');
  setValue(map['postal-code'], suggestion.postcode || '');
}

/**
 * Gets the current input values for address fields given a form and a reference input.
 *
 * @param form - HTML form that includes the autocomplete-compliant input fields
 * @param ref - An input element within the desired form address section
 * @returns A object mapping WHATWG autocomplete properties to their respective form field values
 *
 * @example
 * ```typescript
 * const form = document.querySelector(form);
 * const input = form.querySelector('input[autocomplete~="street-address"]')
 * const valueMap = getFormAutofillValues(form, input);
 * console.log(valueMap);
 * // {
 * //   "street-address": "123 Main",
 * //   "address-level2": "Boston",
 * //   "address-level1": "MA",
 * //   "postal-code": "02129"
 * // }
 * ```
 */
export function getFormAutofillValues(
  form: HTMLFormElement,
  ref: HTMLInputElement
): AutofillValueMap {
  const map = findAddressAutofillInputs(form, ref);
  const values: AutofillValueMap = {};
  for (const [key, input] of Object.entries(map)) {
    if (input?.value) {
      values[key] = input.value;
    }
  }

  return values;
}

/**
 * Converts an {@link AutofillValueMap} to a single line, suitable for
 * display in a text field.
 *
 * @param snapshot - An object mapping WHATWG autocomplete attribute values to their corresponding input field values
 * @returns A concatenated address string
 *
 * @example
 * ```typescript
 * const values = {
 *   'street-address': '123 Main St',
 *   'address-level1': 'CA',
 *   'address-level2': 'San Francisco',
 *   'address-level3': '',
 * };
 *
 * const searchText = getAutofillSearchText(values);
 * console.log(searchText); // '123 Main St, San Francisco, CA'
 * ```
 */
export function getAutofillSearchText(snapshot: AutofillValueMap): string {
  const searchText = [];

  if (snapshot['street-address']) {
    searchText.push(snapshot['street-address']);
  } else {
    searchText.push(snapshot['address-line1'] || '');
    searchText.push(snapshot['address-line2'] || '');
    searchText.push(snapshot['address-line3'] || '');
  }

  searchText.push(snapshot['address-level3'] || '');
  searchText.push(snapshot['address-level2'] || '');
  searchText.push(snapshot['address-level1'] || '');
  searchText.push(snapshot['postal-code'] || '');

  if (snapshot['country-name']) {
    searchText.push(snapshot['country-name']);
  } else {
    searchText.push(snapshot['country'] || '');
  }

  return searchText.filter((part) => Boolean(part)).join(', ');
}

export function fillFormWithFeature(
  feature: AutofillFeatureSuggestion,
  input: HTMLInputElement
): void {
  const form = findParentForm(input);
  if (!form) {
    return;
  }

  const suggestion = featureToSuggestion(feature);
  setFormAutofillValues(form, input, suggestion);

  const inputMap = findAddressAutofillInputs(form, input);
  // TODO: check if suggestion has "missing_unit" property true
  if (inputMap['address-line2']) {
    inputMap['address-line2'].focus();
  }
}

/**
 * Gets address property values from an Autofill feature.
 *
 * @param feature - A {@link AutofillFeatureSuggestion} object.
 * @returns A object mapping WHATWG autocomplete properties to their respective feature values
 */
export function featureToAutofillValueMap(
  feature: AutofillFeatureSuggestion
): AutofillValueMap {
  const values: AutofillValueMap = {};

  const streetAddress = [
    feature.properties.address_line1,
    feature.properties.address_line2,
    feature.properties.address_line3
  ]
    .filter((part) => Boolean(part))
    .join(', ');

  values['street-address'] = streetAddress;
  values['address-line1'] = feature.properties.address_line1;
  values['address-line2'] = feature.properties.address_line2;
  values['address-line3'] = feature.properties.address_line3;
  values['address-level1'] = feature.properties.address_level1;
  values['address-level2'] = feature.properties.address_level2;
  values['address-level3'] = feature.properties.address_level3;
  values['country'] = feature.properties.metadata?.iso_3166_1;
  values['country-name'] = feature.properties.country;
  values['postal-code'] = feature.properties.postcode;

  return values;
}

/**
 * Checks if WHATWG address values from one object match those from another.
 *
 * Can be used to compare an HTML form state against the most recently retrieved Autofill feature
 * to determine if a form has been manually edited after a previous Autofill event.
 *
 * @param targetMap - An {@link AutofillValueMap} object.
 * @param referenceMap - An {@link AutofillValueMap} object.
 * @returns False if all values from the target map are equal to their corresponding value from the reference map.
 */
export function checkAutofillValuesChanged(
  targetMap: AutofillValueMap,
  referenceMap: AutofillValueMap
): boolean {
  for (const [key, value] of Object.entries(targetMap)) {
    if (referenceMap[key] !== value) return true;
  }
  return false;
}

/**
 * Returns the string from suggestions that meant to be used as first stroke
 * title in search results list for that particular suggestion.
 */
export const getSuggestionTitle = (
  item: SearchSuggestion | AutofillSuggestion | GeocodeFeature
): string => {
  return 'address_line1' in item
    ? item.address_line1 || item.matching_name || item.feature_name
    : 'matching_name' in item
    ? item.matching_name || item.feature_name
    : item.place_name.split(',')[0];
};

/**
 * Takes the suggestions array and filters streets inside it to leave only unique ones
 */
export const distinctExactStreetResults = (
  suggestions: (SearchSuggestion | AutofillSuggestion | GeocodeFeature)[]
): (SearchSuggestion | AutofillSuggestion | GeocodeFeature)[] => {
  return suggestions.filter((item1, idx, arr) => {
    const title = getSuggestionTitle(item1);
    return (
      item1.accuracy !== 'street' ||
      arr.findIndex((item2) => title === getSuggestionTitle(item2)) === idx
    );
  });
};

/**
 * Returns parsed/formatted description text to show in the second line of an autofill suggestion element
 */
export const buildSuggestionDescription = (
  item: SearchSuggestion | AutofillSuggestion | GeocodeFeature
): string => {
  return 'description' in item
    ? item.description
    : item.place_name.split(',').splice(1).join(',').trim();
};

export const toggleAutocompletion = (
  input: HTMLInputElement,
  initialAutocompleteValue: string,
  enableBrowserAutocomplete: boolean
): void => {
  /**
   * 'new-password' prevents some browsers from suggestion autocompetion including saved addresses, passwords and etc.
   * https://developer.mozilla.org/en-US/docs/Web/Security/Securing_your_site/Turning_off_form_autocompletion#preventing_autofilling_with_autocompletenew-password
   */
  const disableValue = 'new-password';
  const defaultFallbackValue = 'address-line1';

  const autocompleteValue = enableBrowserAutocomplete
    ? initialAutocompleteValue || defaultFallbackValue
    : disableValue;

  if (input) {
    input.autocomplete = autocompleteValue;
  }
};

export const handleStreetSelection = (
  input: HTMLInputElement,
  initialAutocompleteValue: string,
  suggestion: AutofillSuggestion
): void => {
  if (!input || !suggestion) {
    return;
  }

  // autocomplete attribute is required by fillFormWithFeature method.
  toggleAutocompletion(input, initialAutocompleteValue, true);

  const feature: AutofillFeatureSuggestion = {
    properties: {
      ...suggestion,
      address_line1: suggestion.address_line1 + ' ',
      postcode: null
    }
  } as any;

  fillFormWithFeature(feature, input);

  // turn off browser autocomplete back
  toggleAutocompletion(input, initialAutocompleteValue, false);

  input?.focus();
};
import {
  Suggestion as SearchSuggestion,
  AutofillSuggestion,
  GeocodeFeature
} from '@mapbox/search-js-core';

import { HTMLScopedElement } from './HTMLScopedElement';

import { Theme, getThemeCSS, getIcon } from '../theme';
import { bindElements, getChildElements } from '../utils/dom';
import { Popover, PopoverOptions } from '../utils/popover';

import style from '../style.css';
import { MapboxHTMLEvent } from '../MapboxHTMLEvent';
import { MapboxAddressAutofill } from './MapboxAddressAutofill';
import { AutofillInstance } from '../autofill';
import { getAriaMessage, setLiveRegionMessage } from '../utils/aria';
import { LISTBOX_TEMPLATE, LISTBOX_SUGGESTION_TEMPLATE } from '../constants';
import {
  buildSuggestionDescription,
  getSuggestionTitle
} from '../utils/autofill';

function getAriaIdForSuggestion(resultListId: string, i: number): string {
  return `${resultListId}-${i}`;
}

type Binding = {
  /**
   * Wrapper around the entire component.
   */
  MapboxSearch: HTMLElement;
  /**
   * Results container, which contains:
   * - {@link ResultsList}
   * - {@link Label}
   * - Attribution.
   */
  Results: HTMLDivElement;
  /**
   * Exposed as a listbox to assistive technologies.
   */
  ResultsList: HTMLUListElement;
  /**
   * Exposed as a label.
   *
   * Visually hidden but can "announce" the current selection to
   * assistive technologies.
   */
  Label: HTMLDivElement;
};

type ListboxEventTypes<Suggestion> = {
  /**
   * Triggered on `input` events, i.e. typing  in the `<input>` element.
   */
  input: MapboxHTMLEvent<string>;
  /**
   * Triggered on selection events, i.e. clicking on an item in the results list
   * or hitting `Enter` with an item highlighted.
   */
  select: MapboxHTMLEvent<Suggestion>;
  /**
   * Triggered on `blur` event, i.e. focus moves away from the `<input>` element.
   */
  blur: MapboxHTMLEvent<unknown>;
  /**
   * Triggered on `focus` evenet, i.e. focus moves onto the `<input>` element.
   */
  focus: MapboxHTMLEvent<unknown>;
};

export class MapboxSearchListbox<
  Suggestion extends SearchSuggestion | AutofillSuggestion | GeocodeFeature
> extends HTMLScopedElement<ListboxEventTypes<Suggestion>> {
  protected override get template(): HTMLTemplateElement {
    return LISTBOX_TEMPLATE;
  }

  protected override get templateStyle(): string {
    return style;
  }

  protected get templateUserStyle(): string {
    return getThemeCSS('.MapboxSearch', this.theme);
  }

  /**
   * The current list of suggestions to be rendered in the results box.
   */
  suggestions: Suggestion[] | null = null;

  /**
   * The Autofill instance driving the listbox.
   * Can be either the custom element or function.
   */
  autofillHost?: MapboxAddressAutofill | AutofillInstance;

  #popover: Popover | null = null;

  #binding: Binding;

  #labelID: string;
  #resultListID: string;

  #inputInternal: HTMLInputElement | null;

  get input(): HTMLInputElement | null {
    return this.#inputInternal;
  }

  set input(newInput: HTMLInputElement | null) {
    const oldInput = this.#inputInternal;

    if (oldInput) {
      oldInput.removeEventListener('input', this.#handleInput);
      oldInput.removeEventListener('focus', this.#handleFocus);
      oldInput.removeEventListener('blur', this.#handleBlur);
      oldInput.removeEventListener('keydown', this.#handleKeyDown);

      if (this.#popover) {
        this.#popover.destroy();
      }
    }

    if (newInput) {
      newInput.addEventListener('input', this.#handleInput);
      newInput.addEventListener('focus', this.#handleFocus);
      newInput.addEventListener('blur', this.#handleBlur);
      newInput.addEventListener('keydown', this.#handleKeyDown);

      // Set ARIA role and attributes.
      newInput.setAttribute('role', 'combobox');
      newInput.setAttribute('aria-autocomplete', 'list');
      newInput.setAttribute('aria-controls', this.#resultListID);

      if (this.isConnected) {
        this.#popover = new Popover(
          newInput,
          this.#binding.Results,
          this.popoverOptions
        );
      }
    }

    this.#inputInternal = newInput;
  }

  #selectedIndexInternal = undefined;

  get selectedIndex(): number | undefined {
    return this.#selectedIndexInternal;
  }

  set selectedIndex(newIndex: number) {
    const oldIndex = this.#selectedIndexInternal;
    this.#selectedIndexInternal = newIndex;

    // Update accessibility flags.
    const { ResultsList } = this.#binding;

    const id = getAriaIdForSuggestion(this.#resultListID, newIndex);

    if (newIndex !== undefined) {
      this.input.setAttribute('aria-activedescendant', id);
      ResultsList.setAttribute('aria-activedescendant', id);
    } else {
      this.input.removeAttribute('aria-activedescendant');
      ResultsList.removeAttribute('aria-activedescendant');
    }

    // Update the selected suggestion.
    if (oldIndex !== newIndex) {
      const oldId = getAriaIdForSuggestion(this.#resultListID, oldIndex);
      const oldEl = ResultsList.querySelector(`#${oldId}`);
      oldEl?.removeAttribute('aria-selected');
      oldEl?.setAttribute('tabindex', '-1');

      if (newIndex !== undefined) {
        const el = ResultsList.querySelector(`#${id}`) as HTMLElement;
        el?.setAttribute('aria-selected', 'true');
        el?.setAttribute('tabindex', '0');
        // TODO: uncomment after moving hideResults logic out from handleBlur and delete aria-activedescendant logic
        // el?.focus();
      }
    }

    this.renderAriaMessage();
  }

  #showResults(): void {
    if (!this.suggestions || !this.suggestions.length) {
      return;
    }

    const { Results, MapboxSearch } = this.#binding;

    // Calculate width, enable display
    const rect = this.input.getBoundingClientRect();
    MapboxSearch.style.setProperty('--width', `${rect.width}px`);
    MapboxSearch.style.setProperty('display', 'block');

    // Update accessibility flags.
    this.input.setAttribute('aria-expanded', 'true');
    Results.removeAttribute('aria-hidden');
    // Reset selected index.
    this.selectedIndex = undefined;
  }

  hideResults(): void {
    const { Results, ResultsList } = this.#binding;

    // Update accessibility flags.
    Results.setAttribute('aria-hidden', 'true');
    this.input.setAttribute('aria-expanded', 'false');
    ResultsList.removeAttribute('aria-activedescendant');
    this.input.removeAttribute('aria-activedescendant');
  }

  renderItem(i: number): HTMLElement {
    const element = this.prepareTemplate(LISTBOX_SUGGESTION_TEMPLATE);
    element.id = getAriaIdForSuggestion(this.#resultListID, i);

    return element;
  }

  fillItem(
    el: Element,
    item: Suggestion,
    i: number,
    totalLength: number
  ): void {
    const iconEl = el.querySelector('[class$="SuggestionIcon"]');
    const nameEl = el.querySelector('[class$="SuggestionName"]');
    const descriptionEl = el.querySelector('[class$="SuggestionDesc"]');

    if (item.accuracy) {
      iconEl.innerHTML = getIcon(
        item.accuracy === 'street' ? 'street' : 'addressMarker',
        this.theme
      );
      iconEl.removeAttribute('aria-hidden');
    } else {
      iconEl.setAttribute('aria-hidden', 'true');
    }

    // reset previous values
    nameEl.textContent = descriptionEl.textContent = '';

    nameEl.textContent = getSuggestionTitle(item);

    descriptionEl.textContent = buildSuggestionDescription(item);

