type Binding = {
  MapboxAddressConfirmationFeature: HTMLDivElement;
  Modal: HTMLDivElement;
  ModalHeaderTitle: HTMLDivElement;
  ModalMap: HTMLDivElement;
  Minimap: MapboxAddressMinimap;

  IconQuestion: SVGSVGElement;
